script:
frappe.ui.form.on('Quotation', {
	refresh(frm) {
	    frm.trigger("update_party_name_query")
	},
	quotation_to(frm) {
	    frm.trigger("update_party_name_query")
	},
	update_party_name_query(){
	    if(cur_frm.doc.quotation_to == "Customer"){
    		cur_frm.set_query("party_name", function () {
    				return {
    					filters: {
    						company: ["=", cur_frm.doc.company],
    					},
    				};
    		});
	    }
	    else{
            cur_frm.fields_dict.party_name.get_query = null;
	    }
	}
})
